<!DOCTYPE html>
<html>
<head>
    <meta charset="UTF-8">
    <style>
        body {
            font-family: -apple-system, BlinkMacSystemFont, 'Segoe UI', Roboto, sans-serif;
            background: linear-gradient(135deg, #f5f7fa 0%, #c3cfe2 100%);
            margin: 0;
            padding: 20px;
        }
        .container {
            max-width: 1200px;
            margin: 0 auto;
        }
        .header {
            text-align: center;
            margin-bottom: 2rem;
        }
        .title {
            font-size: 2.5rem;
            color: #2d3748;
            margin-bottom: 0.5rem;
            display: flex;
            align-items: center;
            justify-content: center;
            gap: 10px;
        }
        .subtitle {
            font-size: 1.25rem;
            color: #4a5568;
            margin-bottom: 1.5rem;
        }
        .progress-container {
            max-width: 600px;
            margin: 0 auto 1.5rem;
        }
        .progress-header {
            display: flex;
            justify-content: space-between;
            margin-bottom: 0.5rem;
            font-size: 0.875rem;
            color: #4a5568;
        }
        .progress-bar {
            width: 100%;
            height: 12px;
            background-color: #e2e8f0;
            border-radius: 6px;
            overflow: hidden;
        }
        .progress-fill {
            height: 100%;
            background: linear-gradient(90deg, #3182ce, #805ad5);
            width: 0%;
            transition: width 0.3s ease;
        }
        .badge {
            display: inline-flex;
            align-items: center;
            gap: 8px;
            padding: 8px 16px;
            border-radius: 25px;
            background: white;
            border: 2px solid #e2e8f0;
            font-weight: 600;
            margin-bottom: 2rem;
        }
        .mission {
            background: white;
            border-radius: 8px;
            padding: 1.5rem;
            margin-bottom: 2rem;
            box-shadow: 0 4px 6px rgba(0, 0, 0, 0.1);
            border-left: 4px solid #3182ce;
        }
        .mission h2 {
            color: #2d3748;
            margin-bottom: 1rem;
            font-size: 1.5rem;
        }
        .mission p {
            color: #4a5568;
            line-height: 1.6;
            font-size: 1.1rem;
        }
        .sections-grid {
            display: grid;
            grid-template-columns: repeat(auto-fit, minmax(500px, 1fr));
            gap: 1.5rem;
        }
        .section-card {
            background: white;
            border-radius: 8px;
            padding: 1.5rem;
            border: 2px solid #e2e8f0;
            transition: all 0.2s ease;
            position: relative;
        }
        .section-card:hover {
            box-shadow: 0 8px 16px rgba(0, 0, 0, 0.1);
        }
        .section-header {
            display: flex;
            justify-content: between;
            align-items: flex-start;
            margin-bottom: 1rem;
        }
        .section-title {
            flex: 1;
        }
        .section-title h3 {
            color: #2d3748;
            margin: 0 0 0.25rem 0;
            font-size: 1.25rem;
        }
        .section-subtitle {
            color: #4a5568;
            font-size: 0.875rem;
            margin: 0;
        }
        .checkbox {
            width: 24px;
            height: 24px;
            border: 2px solid #cbd5e0;
            border-radius: 50%;
            cursor: pointer;
            display: flex;
            align-items: center;
            justify-content: center;
            transition: all 0.2s ease;
            margin-left: 1rem;
            flex-shrink: 0;
        }
        .checkbox:hover {
            border-color: #3182ce;
        }
        .checkbox.completed {
            background: #48bb78;
            border-color: #48bb78;
            color: white;
        }
        .section-description {
            color: #4a5568;
            margin-bottom: 1rem;
            line-height: 1.5;
        }
        .tasks-list {
            margin-bottom: 1rem;
        }
        .tasks-list h4 {
            color: #2d3748;
            margin: 0 0 0.5rem 0;
            font-size: 0.875rem;
            font-weight: 600;
        }
        .task-item {
            display: flex;
            align-items: center;
            gap: 8px;
            margin-bottom: 4px;
            font-size: 0.875rem;
            color: #4a5568;
        }
        .task-bullet {
            width: 6px;
            height: 6px;
            background: #cbd5e0;
            border-radius: 50%;
        }
        .section-footer {
            display: flex;
            justify-content: space-between;
            align-items: center;
            padding-top: 1rem;
            border-top: 1px solid #e2e8f0;
            font-size: 0.875rem;
        }
        .reward {
            color: #4a5568;
            display: flex;
            align-items: center;
            gap: 4px;
        }
        .time {
            color: #718096;
        }
        .completion-celebration {
            background: linear-gradient(135deg, #fef5e7, #fed7aa);
            border: 2px solid #f6ad55;
            border-radius: 8px;
            padding: 2rem;
            text-align: center;
            margin-top: 2rem;
            display: none;
        }
        .completion-celebration.show {
            display: block;
        }
        .trophy {
            font-size: 4rem;
            margin-bottom: 1rem;
        }
        .next-steps {
            background: white;
            border-radius: 8px;
            padding: 1.5rem;
            margin-top: 2rem;
            box-shadow: 0 4px 6px rgba(0, 0, 0, 0.1);
        }
        .next-steps h2 {
            color: #2d3748;
            margin-bottom: 1rem;
        }
        .prerequisites-grid {
            display: grid;
            grid-template-columns: repeat(auto-fit, minmax(250px, 1fr));
            gap: 1rem;
            margin-bottom: 1.5rem;
        }
        .prereq-card {
            background: #f7fafc;
            border-radius: 6px;
            padding: 1rem;
        }
        .start-button {
            background: linear-gradient(90deg, #3182ce, #805ad5);
            color: white;
            border: none;
            padding: 12px 24px;
            border-radius: 6px;
            font-size: 1rem;
            font-weight: 600;
            cursor: pointer;
            transition: transform 0.2s ease;
        }
        .start-button:hover {
            transform: translateY(-2px);
        }

        /* Color themes for sections */
        .blue { background: #ebf8ff; border-color: #90cdf4; }
        .green { background: #f0fff4; border-color: #9ae6b4; }
        .purple { background: #faf5ff; border-color: #d6bcfa; }
        .yellow { background: #fffbeb; border-color: #f6e05e; }
    </style>
</head>
<body>
    <div class="container">
        <!-- Header -->
        <div class="header">
            <h1 class="title">
                ‚ö° AI Integration Cookbook ‚ö°
            </h1>
            <p class="subtitle">Your Interactive Journey from Data Novice to AI Integration Expert</p>

            <!-- Progress Bar -->
            <div class="progress-container">
                <div class="progress-header">
                    <span>Progress</span>
                    <span id="progress-text">0/4 Complete</span>
                </div>
                <div class="progress-bar">
                    <div class="progress-fill" id="progress-fill"></div>
                </div>
            </div>

            <!-- Current Badge -->
            <div class="badge" id="current-badge">
                <span>üéØ</span>
                <span>Current Status: Getting Started</span>
            </div>
        </div>

        <!-- Mission Statement -->
        <div class="mission">
            <h2>üéØ Your Mission</h2>
            <p>
                Welcome to your AI integration journey! Using the adorable Palmer Penguins dataset, you'll master the complete 
                data science workflow. By the end of this cookbook, you'll have the skills and confidence to integrate AI 
                into your own projects and workflows. Ready to become an AI Integration Expert?
            </p>
        </div>

        <!-- Learning Path -->
        <div class="sections-grid">
            <div class="section-card blue">
                <div class="section-header">
                    <div class="section-title">
                        <h3>üéØ Project Overview</h3>
                        <p class="section-subtitle">Understanding the Palmer Penguins Challenge</p>
                    </div>
                    <div class="checkbox" data-section="overview">‚úì</div>
                </div>
                <p class="section-description">
                    Learn about our dataset, goals, and the AI workflow we'll master together.
                </p>
                <div class="tasks-list">
                    <h4>What You'll Accomplish:</h4>
                    <div class="task-item"><div class="task-bullet"></div>Meet our penguin friends (Adelie, Chinstrap, Gentoo)</div>
                    <div class="task-item"><div class="task-bullet"></div>Understand the business problem: species classification</div>
                    <div class="task-item"><div class="task-bullet"></div>Review the complete data science workflow</div>
                    <div class="task-item"><div class="task-bullet"></div>Set your learning objectives</div>
                </div>
                <div class="section-footer">
                    <div class="reward">‚≠ê Data Science Detective Badge</div>
                    <div class="time">‚è±Ô∏è 15 minutes</div>
                </div>
            </div>

            <div class="section-card green">
                <div class="section-header">
                    <div class="section-title">
                        <h3>üîç Data Acquisition & Cleaning</h3>
                        <p class="section-subtitle">Getting Your Hands Dirty with Real Data</p>
                    </div>
                    <div class="checkbox" data-section="acquisition">‚úì</div>
                </div>
                <p class="section-description">
                    Master the art of loading, exploring, and cleaning messy real-world data.
                </p>
                <div class="tasks-list">
                    <h4>What You'll Accomplish:</h4>
                    <div class="task-item"><div class="task-bullet"></div>Install and import the palmerpenguins package</div>
                    <div class="task-item"><div class="task-bullet"></div>Load the dataset and explore its structure</div>
                    <div class="task-item"><div class="task-bullet"></div>Identify and handle missing values</div>
                    <div class="task-item"><div class="task-bullet"></div>Perform basic data quality checks</div>
                    <div class="task-item"><div class="task-bullet"></div>Create a clean dataset ready for analysis</div>
                </div>
                <div class="section-footer">
                    <div class="reward">‚≠ê Data Cleaning Specialist Certificate</div>
                    <div class="time">‚è±Ô∏è 45 minutes</div>
                </div>
            </div>

            <div class="section-card purple">
                <div class="section-header">
                    <div class="section-title">
                        <h3>üìä Data Visualization & Exploration</h3>
                        <p class="section-subtitle">Making Data Tell Its Story</p>
                    </div>
                    <div class="checkbox" data-section="visualization">‚úì</div>
                </div>
                <p class="section-description">
                    Create compelling visualizations that reveal hidden patterns and insights.
                </p>
                <div class="tasks-list">
                    <h4>What You'll Accomplish:</h4>
                    <div class="task-item"><div class="task-bullet"></div>Create species distribution plots</div>
                    <div class="task-item"><div class="task-bullet"></div>Build correlation heatmaps</div>
                    <div class="task-item"><div class="task-bullet"></div>Design interactive scatter plots</div>
                    <div class="task-item"><div class="task-bullet"></div>Craft publication-ready visualizations</div>
                    <div class="task-item"><div class="task-bullet"></div>Discover surprising patterns in penguin behavior</div>
                </div>
                <div class="section-footer">
                    <div class="reward">‚≠ê Visualization Virtuoso Medal</div>
                    <div class="time">‚è±Ô∏è 60 minutes</div>
                </div>
            </div>

            <div class="section-card yellow">
                <div class="section-header">
                    <div class="section-title">
                        <h3>üß† AI Model Building & Prediction</h3>
                        <p class="section-subtitle">Building Your First AI Classification System</p>
                    </div>
                    <div class="checkbox" data-section="prediction">‚úì</div>
                </div>
                <p class="section-description">
                    Train machine learning models to automatically classify penguin species.
                </p>
                <div class="tasks-list">
                    <h4>What You'll Accomplish:</h4>
                    <div class="task-item"><div class="task-bullet"></div>Split data into training and testing sets</div>
                    <div class="task-item"><div class="task-bullet"></div>Train your first decision tree classifier</div>
                    <div class="task-item"><div class="task-bullet"></div>Compare multiple AI algorithms</div>
                    <div class="task-item"><div class="task-bullet"></div>Optimize model performance</div>
                    <div class="task-item"><div class="task-bullet"></div>Deploy your AI system for real predictions</div>
                </div>
                <div class="section-footer">
                    <div class="reward">‚≠ê AI Integration Master Certification</div>
                    <div class="time">‚è±Ô∏è 90 minutes</div>
                </div>
            </div>
        </div>

        <!-- Completion Celebration -->
        <div class="completion-celebration" id="celebration">
            <div class="trophy">üèÜ</div>
            <h2>üéâ Congratulations!</h2>
            <p style="font-size: 1.25rem; margin-bottom: 1rem;">
                You are now <strong>AI Integration Certified</strong>!
            </p>
            <p>
                You've mastered the complete data science workflow and are ready to integrate AI into your own projects. 
                Share your achievement and help others on their AI journey!
            </p>
        </div>

        <!-- Next Steps -->
        <div class="next-steps">
            <h2>üöÄ Ready to Begin?</h2>
            <div class="prerequisites-grid">
                <div class="prereq-card">
                    <h3 style="margin-top: 0;">üìö Prerequisites</h3>
                    <ul style="margin: 0; padding-left: 1.25rem;">
                        <li>Basic Python knowledge (helpful but not required)</li>
                        <li>Jupyter Notebook environment</li>
                        <li>Curiosity and willingness to learn!</li>
                    </ul>
                </div>
                <div class="prereq-card">
                    <h3 style="margin-top: 0;">üéÅ What You'll Get</h3>
                    <ul style="margin: 0; padding-left: 1.25rem;">
                        <li>Hands-on experience with real data</li>
                        <li>Reusable code templates</li>
                        <li>Confidence to tackle your own AI projects</li>
                    </ul>
                </div>
            </div>

            <div style="text-align: center;">
                <button class="start-button">
                    üêß Start Your Penguin Adventure!
                </button>
            </div>
        </div>
    </div>

    <script>
        // Track completion state
        let completedSections = {
            overview: false,
            acquisition: false,
            visualization: false,
            prediction: false
        };

        // Badge definitions
        const badges = [
            { name: "Getting Started", icon: "üéØ", count: 0 },
            { name: "Data Explorer", icon: "üîç", count: 1 },
            { name: "Visualization Master", icon: "üìä", count: 2 },
            { name: "AI Apprentice", icon: "üß†", count: 3 },
            { name: "AI Integration Certified", icon: "üèÜ", count: 4 }
        ];

        function updateProgress() {
            const completed = Object.values(completedSections).filter(v => v).length;
            const total = Object.keys(completedSections).length;
            const percentage = (completed / total) * 100;

            // Update progress bar
            document.getElementById('progress-fill').style.width = percentage + '%';
            document.getElementById('progress-text').textContent = `${completed}/${total} Complete`;

            // Update badge
            const currentBadge = badges.find(badge => badge.count === completed);
            document.getElementById('current-badge').innerHTML = 
                `<span>${currentBadge.icon}</span><span>Current Status: ${currentBadge.name}</span>`;

            // Show celebration if complete
            if (completed === total) {
                document.getElementById('celebration').classList.add('show');
            } else {
                document.getElementById('celebration').classList.remove('show');
            }
        }

        // Add click handlers to checkboxes
        document.querySelectorAll('.checkbox').forEach(checkbox => {
            checkbox.addEventListener('click', function() {
                const section = this.dataset.section;
                completedSections[section] = !completedSections[section];

                // Update visual state
                if (completedSections[section]) {
                    this.classList.add('completed');
                } else {
                    this.classList.remove('completed');
                }

                updateProgress();
            });
        });

        // Initialize
        updateProgress();
    </script>
</body>
</html>
""")
